// ==UserScript==
// @name         Alerta de Preço KuCoin Futures Grid (Aprimorado)
// @namespace    http://tampermonkey.net/
// @version      1.5
// @description  Monitora o preço de um SPAN específico na KuCoin, com modal de configuração arrastável e alertas visuais/sonoros.
// @author       Seu Nome
// @match        https://www.kucoin.com/trading-bot/futures/grid/XBTUSDTM
// @grant        GM_setValue
// @grant        GM_getValue
// @grant        GM_addStyle
// ==/UserScript==

/*
    background kucoin: rgb(1, 188, 141)
    text-kucoin: rgb(29, 29, 29)
*/

(function() {
    'use strict';

    // --- Variáveis de Configuração e Alerta ---
    let precoLimiteSuperior = GM_getValue('precoLimiteSuperior', '');
    let precoLimiteInferior = GM_getValue('precoLimiteInferior', '');

    let jaAlertouAcima = false;
    let jaAlertouAbaixo = false;

    const KEY_PRECO_SUPERIOR = 'precoLimiteSuperior';
    const KEY_PRECO_INFERIOR = 'precoLimiteInferior';

    const SOM_ALERTA_UP = 'data:audio/mpeg;base64,';
    const SOM_ALERTA_DOWN = 'data:audio/mpeg;base64,';

    let audioContextAtivado = false;

    console.log("Script Tampermonkey iniciado.");
    console.log(`Configuração inicial - Superior: "${precoLimiteSuperior}", Inferior: "${precoLimiteInferior}"`);

    // --- Funções de Utilitário ---

    function obterEFormatarPreco() {
        // Seletor mais robusto, focando no ID do pai e na classe de tamanho de fonte do preço principal.
        const spanPreco = document.querySelector('#ChartHeadArea span.fs-18');

        if (spanPreco) {
            const textoPreco = spanPreco.innerText.replace(/,/g, '');
            const preco = parseFloat(textoPreco);
            console.log(`Preço lido do SPAN: "${spanPreco.innerText}" -> Formatado: ${preco}`);
            return isNaN(preco) ? null : preco;
        }
        console.log("SPAN do preço NÃO encontrado na DOM com o seletor atual (#ChartHeadArea span.fs-18).");
        return null;
    }

    function tocarSom(url) {
        if (!audioContextAtivado) {
            console.log("Contexto de áudio não ativado. Tentando ativá-lo com clique do usuário.");
            return;
        }
        const audio = new Audio(url);
        audio.play().catch(e => console.error("Erro ao tocar som:", e));
    }

    function ativarContextoAudio() {
        if (audioContextAtivado) return;

        // Tenta tocar um som mudo para "ativar" o áudio no navegador
        const audio = new Audio(SOM_ALERTA_UP); // Usa um dos sons para tentar ativar
        audio.volume = 0.01; // Quase mudo
        audio.play()
            .then(() => {
                console.log("Contexto de áudio ativado com sucesso!");
                audioContextAtivado = true;
                audio.pause(); // Pausa imediatamente após ativar
                audio.currentTime = 0;
            })
            .catch(e => {
                console.warn("Não foi possível ativar o contexto de áudio automaticamente. Necessita interação do usuário:", e);
                // Permanece audioContextAtivado = false;
            });
    }

    // --- Funções de Interface (Modal de Alerta Personalizado) ---

    function criarModalAlerta() {
        const modalId = 'tm-custom-alert-modal';
        let modal = document.getElementById(modalId);

        if (!modal) {
            modal = document.createElement('div');
            modal.id = modalId;
            modal.innerHTML = `
                <div class="tm-modal-content">
                    <p id="tm-alert-message"></p>
                    <button id="tm-alert-ok-button">OK</button>
                </div>
            `;
            document.body.appendChild(modal);

            document.getElementById('tm-alert-ok-button').addEventListener('click', () => {
                modal.style.display = 'none';
            });
        }
        return modal;
    }

    function exibirAlertaPersonalizado(mensagem, tipoAlerta) {
        const modal = criarModalAlerta();
        document.getElementById('tm-alert-message').innerText = mensagem;
        modal.style.display = 'block';

        if (tipoAlerta === 'up') {
            tocarSom(SOM_ALERTA_UP);
        } else if (tipoAlerta === 'down') {
            tocarSom(SOM_ALERTA_DOWN);
        }
        console.log(`Alerta disparado: ${mensagem}`);
    }

    // --- Funções de Salvar Configuração ---
    function salvarConfiguracoes() {
        const inputSuperior = document.getElementById('inputPrecoSuperior');
        const inputInferior = document.getElementById('inputPrecoInferior');

        const novoSuperior = inputSuperior.value;
        const novoInferior = inputInferior.value;

        GM_setValue(KEY_PRECO_SUPERIOR, novoSuperior);
        GM_setValue(KEY_PRECO_INFERIOR, novoInferior);

        precoLimiteSuperior = novoSuperior;
        precoLimiteInferior = novoInferior;

        console.log(`Configurações salvas - Superior: "${precoLimiteSuperior}", Inferior: "${precoLimiteInferior}"`);

        // As flags de alerta são resetadas para que novos alertas possam disparar
        jaAlertouAcima = false;
        jaAlertouAbaixo = false;
    }

    // --- Funções de Interface (Modal de Configuração) ---

    function criarModalConfiguracao() {
        const modalId = 'tm-config-modal';
        let modal = document.getElementById(modalId);

        if (!modal) {
            modal = document.createElement('div');
            modal.id = modalId;
            modal.innerHTML = `
                <div class="tm-modal-header" id="tm-modal-header">Configurações de Alerta</div>
                <div class="tm-modal-body">
                    <label for="inputPrecoSuperior">Preço Limite Superior:</label>
                    <input type="number" id="inputPrecoSuperior" step="0.1" placeholder="Ex: 110000.0"><br>
                    <label for="inputPrecoInferior">Preço Limite Inferior:</label>
                    <input type="number" id="inputPrecoInferior" step="0.1" placeholder="Ex: 100000.0"><br>
                    <button id="btnSalvarConfig">Salvar</button>
                    <button id="btnFecharConfig">Fechar</button>
                </div>
            `;
            document.body.appendChild(modal);

            const inputSuperior = document.getElementById('inputPrecoSuperior');
            const inputInferior = document.getElementById('inputPrecoInferior');
            const btnSalvar = document.getElementById('btnSalvarConfig');
            const btnFechar = document.getElementById('btnFecharConfig');

            // Carregar valores salvos ao abrir o modal
            inputSuperior.value = GM_getValue(KEY_PRECO_SUPERIOR, '');
            inputInferior.value = GM_getValue(KEY_PRECO_INFERIOR, '');

            // Evento de Salvar (clique no botão Salvar)
            btnSalvar.addEventListener('click', () => {
                salvarConfiguracoes();
                modal.style.display = 'none'; // Fechar modal após salvar
            });

            // Evento de Salvar (tecla Enter nas inputs)
            inputSuperior.addEventListener('keydown', (e) => {
                if (e.key === 'Enter') {
                    salvarConfiguracoes();
                    modal.style.display = 'none';
                }
            });
            inputInferior.addEventListener('keydown', (e) => {
                if (e.key === 'Enter') {
                    salvarConfiguracoes();
                    modal.style.display = 'none';
                }
            });

            // Evento de Fechar
            btnFechar.addEventListener('click', () => {
                modal.style.display = 'none';
            });

            // Tornar o modal arrastável
            criarElementoArrastavel(modal, document.getElementById('tm-modal-header'));
        }
        return modal;
    }

    // Botão para abrir o modal de configuração
    function criarBotaoConfiguracao() {
        const btnId = 'tm-open-config-btn';
        let btn = document.getElementById(btnId);

        if (!btn) {
            btn = document.createElement('button');
            btn.id = btnId;
            btn.innerText = '⚙️ Alertas';
            btn.style.cssText = `
                position: fixed;
                top: 10px;
                right: 10px;
                z-index: 99999;
                background-color: rgb(1, 188, 141); /* Cor de fundo da KuCoin */
                color: rgb(29, 29, 29); /* Cor do texto da KuCoin */
                border: none;
                padding: 8px 12px;
                border-radius: 5px;
                cursor: pointer;
                font-size: 14px;
            `;
            document.body.appendChild(btn);

            // Torna o botão arrastável e cuida do clique
            criarElementoArrastavel(btn, btn, () => {
                const modal = criarModalConfiguracao();
                modal.style.display = 'block';
                ativarContextoAudio(); // Tenta ativar áudio no clique do botão
            });
        }
    }

    // Função genérica para tornar um elemento arrastável e lidar com cliques
    function criarElementoArrastavel(elementoArrastavel, handle, clickCallback = null) {
        let isDragging = false;
        let startX, startY;
        let originalX, originalY;
        let moved = false;
        const DRAG_THRESHOLD = 5; // Pixels para considerar um arrasto

        handle.addEventListener('mousedown', (e) => {
            isDragging = true;
            moved = false;
            startX = e.clientX;
            startY = e.clientY;

            const rect = elementoArrastavel.getBoundingClientRect();
            originalX = rect.left;
            originalY = rect.top;

            elementoArrastavel.style.position = 'fixed';
            elementoArrastavel.style.right = 'auto';
            elementoArrastavel.style.bottom = 'auto';
            elementoArrastavel.style.transform = 'none';

            elementoArrastavel.style.cursor = 'grabbing';
            elementoArrastavel.style.userSelect = 'none';
            elementoArrastavel.style.transition = 'none';
        });

        document.addEventListener('mouseup', (e) => {
            if (isDragging) {
                isDragging = false;
                elementoArrastavel.style.cursor = 'grab';
                elementoArrastavel.style.userSelect = '';
                elementoArrastavel.style.transition = '';

                if (!moved && clickCallback && Math.abs(e.clientX - startX) < DRAG_THRESHOLD && Math.abs(e.clientY - startY) < DRAG_THRESHOLD) {
                    clickCallback();
                }
            }
        });

        document.addEventListener('mousemove', (e) => {
            if (isDragging) {
                e.preventDefault();
                const newX = originalX + (e.clientX - startX);
                const newY = originalY + (e.clientY - startY);

                elementoArrastavel.style.left = `${newX}px`;
                elementoArrastavel.style.top = `${newY}px`;
                moved = true;
            }
        });
    }

    // --- Estilos CSS para os Modais ---
    GM_addStyle(`
        #tm-config-modal, #tm-custom-alert-modal {
            display: none;
            position: fixed;
            z-index: 100000;
            background-color: rgba(0,0,0,0.7);
            padding: 0;
            border-radius: 8px;
            box-shadow: 0 4px 8px rgba(0,0,0,0.2);
            color: white;
            font-family: Arial, sans-serif;
            box-sizing: border-box;
        }

        #tm-config-modal {
            top: 20px;
            right: 20px;
            left: auto;
            width: 300px;
        }

        #tm-config-modal .tm-modal-header {
            padding: 10px;
            background-color: #333; /* Cor de fundo do cabeçalho do modal */
            border-bottom: 1px solid #444;
            cursor: move;
            border-top-left-radius: 8px;
            border-top-right-radius: 8px;
            font-weight: bold;
            text-align: center;
        }

        #tm-config-modal .tm-modal-body {
            padding: 15px;
            display: flex;
            flex-direction: column;
            gap: 10px;
        }

        #tm-config-modal label {
            margin-bottom: 5px;
            font-size: 14px;
        }

        #tm-config-modal input[type="number"] {
            width: calc(100% - 20px);
            padding: 8px;
            border: 1px solid #555;
            border-radius: 4px;
            background-color: #444;
            color: white;
            font-size: 14px;
        }

        #tm-config-modal button {
            padding: 10px 15px;
            border: none;
            border-radius: 5px;
            cursor: pointer;
            font-size: 14px;
            margin-top: 5px;
        }

        #tm-config-modal #btnSalvarConfig {
            background-color: rgb(1, 188, 141); /* Cor de fundo do botão Salvar (KuCoin) */
            color: rgb(29, 29, 29); /* Cor do texto do botão Salvar (KuCoin) */
        }

        #tm-config-modal #btnFecharConfig {
            background-color: #6c757d; /* Cinza padrão para Fechar */
            color: white;
        }

        #tm-custom-alert-modal {
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%);
            width: 300px;
            text-align: center;
        }

        #tm-custom-alert-modal .tm-modal-content {
            padding: 20px;
            background-color: #222; /* Fundo escuro para o conteúdo do alerta */
            border-radius: 8px;
        }

        #tm-custom-alert-modal p {
            margin-bottom: 20px;
            font-size: 16px;
        }

        #tm-custom-alert-modal button {
            padding: 10px 20px;
            /* Cores para o botão OK do alerta, usando rgba para possível transparência */
            background-color: rgb(1, 188, 141);
            color: rgb(29, 29, 29);
            border: none;
            border-radius: 5px;
            cursor: pointer;
            font-size: 16px;
        }

        #tm-open-config-btn {
            cursor: grab;
        }
    `);

    // --- Lógica Principal de Verificação e Alerta ---
    function verificarPrecoEAlertar(precoAtual) {
        console.log(`Verificando preço: ${precoAtual}`);
        console.log(`Limites atuais: Superior="${precoLimiteSuperior}", Inferior="${precoLimiteInferior}"`);

        const superior = precoLimiteSuperior !== '' ? parseFloat(precoLimiteSuperior) : null;
        const inferior = precoLimiteInferior !== '' ? parseFloat(precoLimiteInferior) : null;

        if (precoAtual === null || (superior === null && inferior === null)) {
            console.log("Preço atual inválido ou nenhum limite definido. Nenhuma ação de alerta.");
            return;
        }

        if (superior !== null && precoAtual > superior && !jaAlertouAcima) {
            exibirAlertaPersonalizado(`📈 ALERTA DE PREÇO ALTO! O preço atingiu ou ultrapassou ${superior.toLocaleString()}! Preço atual: ${precoAtual.toLocaleString()}`, 'up');
            jaAlertouAcima = true;
            jaAlertouAbaixo = false;
        } else if (inferior !== null && precoAtual < inferior && !jaAlertouAbaixo) {
            exibirAlertaPersonalizado(`📉 ALERTA DE PREÇO BAIXO! O preço caiu para ou abaixo de ${inferior.toLocaleString()}! Preço atual: ${precoAtual.toLocaleString()}`, 'down');
            jaAlertouAbaixo = true;
            jaAlertouAcima = false;
        } else if (
            (superior === null || precoAtual <= superior) &&
            (inferior === null || precoAtual >= inferior)
        ) {
            if (jaAlertouAcima || jaAlertouAbaixo) {
                console.log("Preço voltou para a zona segura. Alertas resetados.");
            }
            jaAlertouAcima = false;
            jaAlertouAbaixo = false;
        }
    }

    // --- Inicia o Monitoramento ---
    const seletorSpanPreco = '#ChartHeadArea span.fs-18';
    let elementoSpanPreco = null;

    function iniciarObservador() {
        elementoSpanPreco = document.querySelector(seletorSpanPreco);

        if (elementoSpanPreco) {
            console.log("SPAN do preço ENCONTRADO na DOM com o seletor mais confiável. Iniciando MutationObserver.");
            const observador = new MutationObserver(function(mutacoesLista) {
                for (let mutacao of mutacoesLista) {
                    if (mutacao.type === 'characterData' || (mutacao.type === 'childList' && mutacao.target === elementoSpanPreco)) {
                        const precoAtual = obterEFormatarPreco();
                        verificarPrecoEAlertar(precoAtual);
                    }
                }
            });

            observador.observe(elementoSpanPreco, { characterData: true, subtree: true, childList: true });

            verificarPrecoEAlertar(obterEFormatarPreco());

        } else {
            console.log(`SPAN do preço NÃO encontrado com o seletor "${seletorSpanPreco}". Tentando novamente em 2 segundos...`);
            setTimeout(iniciarObservador, 2000);
        }
    }

    // --- Execução Inicial ---
    criarBotaoConfiguracao();
    iniciarObservador();

    document.addEventListener('click', ativarContextoAudio, { once: true });
    document.addEventListener('keydown', ativarContextoAudio, { once: true });

})();
